import React from 'react';
import { Link } from 'react-router-dom'; // For linking to the CarbonFootprintPage
import './HomePage.css'; // Add this line to import the styles


const HomePage = () => {
    return (
        <div style={{ textAlign: 'center', padding: '50px' }}>
            {/* Hero Section */}
            <div>
                <img
                    src="cb.png" // Replace this with the path to your image
                    alt="Hero Image"
                    style={{ width: '100%', height: 'auto', maxHeight: '500px', objectFit: 'cover' }}
                />
            </div>
            <h1>Welcome to Carbon Footprint Estimator</h1>
            <p>Your one-stop solution for calculating your carbon footprint.</p>
            {/* Button linking to the CarbonFootprintPage */}
            <Link to="/carbon-footprint">
                <button style={{ padding: '10px 20px', fontSize: '16px' }}>
                    Start Estimating
                </button>
            </Link>
        </div>
    );
};

export default HomePage;
