import React, { useState } from 'react';
import './App.css';

function App() {
  const [activity, setActivity] = useState('');
  const [value, setValue] = useState('');
  const [unit, setUnit] = useState('');
  const [result, setResult] = useState(null);

  const handleCalculate = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/carbon-footprint', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ activity, value, unit }),
      });

      if (!response.ok) {
        throw new Error(`HTTP error! Status: ${response.status}`);
      }

      const data = await response.json();
      setResult(data);
    } catch (error) {
      console.error('Network Error:', error);
      alert('An error occurred while calculating. Please try again.');
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Carbon Footprint Calculator</h1>

        <div>
          <label>
            Activity:
            <input
              type="text"
              value={activity}
              onChange={(e) => setActivity(e.target.value)}
              placeholder="e.g., Electricity"
            />
          </label>
        </div>

        <div>
          <label>
            Value:
            <input
              type="number"
              value={value}
              onChange={(e) => setValue(e.target.value)}
              placeholder="e.g., 100"
            />
          </label>
        </div>

        <div>
          <label>
            Unit:
            <select value={unit} onChange={(e) => setUnit(e.target.value)}>
              <option value="">Select Unit</option>
              <option value="kWh">kWh</option>
              <option value="kg">kg</option>
            </select>
          </label>
        </div>

        <button onClick={handleCalculate}>Calculate</button>

        {result && (
          <div>
            <h2>Result</h2>
            <p>Activity: {result.activity}</p>
            <p>Value: {result.value} {result.unit}</p>
            <p>Emissions: {result.emissions} kgCO2e</p>
            <p>{result.message}</p>
          </div>
        )}
      </header>
    </div>
  );
}

export default App;
