import React, { useState } from "react";
import axios from "axios";

const App = () => {
  const [distance, setDistance] = useState("");
  const [transportMode, setTransportMode] = useState("");
  const [result, setResult] = useState(null);
  const [error, setError] = useState("");

  const handleCalculate = async () => {
    if (!distance || !transportMode) {
      setError("Please select a transport mode and enter the distance.");
      return;
    }

    try {
      const response = await axios.post("http://localhost:5000/api/carbon-footprint", {
        distance: Number(distance),
        transportMode,
      });
      setResult(response.data);
      setError(""); // Clear error on successful response
    } catch (err) {
      console.error(err.response?.data || err.message);
      setError("Failed to calculate carbon footprint. Please try again.");
      setResult(null);
    }
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gray-100 p-4">
      <div className="bg-white shadow-lg rounded-2xl p-6 max-w-lg w-full">
        <h1 className="text-2xl font-bold mb-4 text-center text-green-600">
          Carbon Footprint Calculator
        </h1>

        {/* Transport Mode Dropdown */}
        <div className="mb-4">
          <label htmlFor="transportMode" className="block font-medium mb-2">
            Select Transport Mode:
          </label>
          <select
            id="transportMode"
            value={transportMode}
            onChange={(e) => setTransportMode(e.target.value)}
            className="w-full border rounded-lg px-4 py-2"
          >
            <option value="">-- Select --</option>
            <option value="car">Car</option>
            <option value="bus">Bus</option>
            <option value="train">Train</option>
            {/* Add more transport modes as needed */}
          </select>
        </div>

        {/* Distance Input */}
        <div className="mb-4">
          <label htmlFor="distance" className="block font-medium mb-2">
            Enter Distance (in km):
          </label>
          <input
            id="distance"
            type="number"
            value={distance}
            onChange={(e) => setDistance(e.target.value)}
            className="w-full border rounded-lg px-4 py-2"
            placeholder="Enter distance in kilometers"
          />
        </div>

        {/* Error Message */}
        {error && (
          <div className="text-red-600 text-sm mb-4">{error}</div>
        )}

        {/* Calculate Button */}
        <button
          onClick={handleCalculate}
          className="bg-green-500 text-white px-6 py-2 rounded-lg hover:bg-green-600 w-full"
        >
          Calculate
        </button>

        {/* Result Display */}
        {result && (
          <div className="mt-6 bg-gray-50 p-4 rounded-lg shadow-md">
            <h2 className="text-lg font-bold text-gray-800 mb-2">
              Carbon Footprint Result:
            </h2>
            <p className="text-gray-700">
              <strong>Emissions:</strong> {result.co2e} {result.co2e_unit}
            </p>
            <p className="text-gray-700">
              <strong>Source:</strong> {result.emission_factor.source}
            </p>
          </div>
        )}
      </div>
    </div>
  );
};

export default App;
