import React, { useState } from 'react';
import axios from 'axios';

const CarbonFootprint = () => {
  const [distance, setDistance] = useState('');
  const [transportMode, setTransportMode] = useState('');
  const [result, setResult] = useState(null);
  const [error, setError] = useState('');

  const handleCalculate = async () => {
    if (!distance || !transportMode) {
      setError('Please enter distance and select a transport mode.');
      return;
    }

    setError('');
    try {
      const response = await axios.post('http://localhost:5000/api/carbon-footprint', {
        distance,
        transportMode,
      });
      setResult(response.data);
    } catch (err) {
      setError('Failed to calculate emissions. Please try again.');
      console.error(err);
    }
  };

  return (
    <div style={{ textAlign: 'center', marginTop: '50px' }}>
      <h1>Carbon Footprint Calculator</h1>
      <div style={{ marginBottom: '20px' }}>
        <input
          type="number"
          placeholder="Distance (km)"
          value={distance}
          onChange={(e) => setDistance(e.target.value)}
          style={{ padding: '10px', marginRight: '10px' }}
        />
        <select
          value={transportMode}
          onChange={(e) => setTransportMode(e.target.value)}
          style={{ padding: '10px' }}
        >
          <option value="">Select Transport Mode</option>
          <option value="car">Car</option>
          <option value="bus">Bus</option>
          <option value="train">Train</option>
          <option value="flight">Flight</option>
        </select>
      </div>
      <button onClick={handleCalculate} style={{ padding: '10px 20px', cursor: 'pointer' }}>
        Calculate
      </button>

      {error && <p style={{ color: 'red', marginTop: '20px' }}>{error}</p>}

      {result && (
        <div style={{ marginTop: '30px' }}>
          <h3>Results:</h3>
          <p><strong>Transport Mode:</strong> {result.transportMode}</p>
          <p><strong>Distance:</strong> {result.distance} km</p>
          <p><strong>CO2 Emissions:</strong> {result.emissions.toFixed(2)} kg</p>
        </div>
      )}
    </div>
  );
};

export default CarbonFootprint;
