import React, { useState } from "react";
import axios from "axios";

const CarbonFootPrint = () => {
    const [distance, setDistance] = useState("");
    const [transportMode, setTransportMode] = useState("");
    const [emissions, setEmissions] = useState(null);
    const [error, setError] = useState(null);

    const handleCalculate = async () => {
        if (!distance || !transportMode) {
            setError("Please provide both distance and transport mode.");
            return;
        }

        try {
            // Make request to the backend API
            const response = await axios.post("http://localhost:5000/api/carbon-footprint", {
                distance,
                transportMode
            });

            // Handle successful response
            setEmissions(response.data.emissions);
            setError(null);
        } catch (error) {
            // Log and display detailed error message
            console.error("Error in response:", error.response || error.message);
            setError(error.response ? error.response.data.message : "An unexpected error occurred.");
        }
    };

    return (
        <div>
            <h1>Calculate Carbon Footprint</h1>
            <div>
                <label>Distance (in km):</label>
                <input
                    type="number"
                    value={distance}
                    onChange={(e) => setDistance(e.target.value)}
                    placeholder="Enter distance"
                />
            </div>
            <div>
                <label>Transport Mode:</label>
                <input
                    type="text"
                    value={transportMode}
                    onChange={(e) => setTransportMode(e.target.value)}
                    placeholder="Enter transport mode (e.g., car, bus)"
                />
            </div>
            <button onClick={handleCalculate}>Calculate</button>

            {emissions && <div><strong>Estimated CO2 emissions:</strong> {emissions} kg</div>}
            {error && <div style={{ color: "red" }}>{error}</div>}
        </div>
    );
};

export default CarbonFootPrint;
