import React, { useState } from 'react';
import axios from 'axios';

const CarbonFootPrint = () => {
    const [energy, setEnergy] = useState('');
    const [energyUnit, setEnergyUnit] = useState('kWh');
    const [carbonFootprint, setCarbonFootprint] = useState(null);
    const [loading, setLoading] = useState(false);

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);

        try {
            const response = await axios.post('http://localhost:5000/api/carbon-footprint', {
                energy,
                energy_unit: energyUnit
            });

            setCarbonFootprint(response.data);  // Store the response from the server
        } catch (error) {
            console.error('Error:', error);
        } finally {
            setLoading(false);
        }
    };

    return (
        <div>
            <h2>Carbon Footprint Estimator</h2>
            <form onSubmit={handleSubmit}>
                <label>
                    Energy Consumption:
                    <input
                        type="number"
                        value={energy}
                        onChange={(e) => setEnergy(e.target.value)}
                        required
                    />
                </label>
                <br />
                <label>
                    Energy Unit:
                    <select
                        value={energyUnit}
                        onChange={(e) => setEnergyUnit(e.target.value)}
                    >
                        <option value="kWh">kWh</option>
                        <option value="MJ">MJ</option>
                    </select>
                </label>
                <br />
                <button type="submit" disabled={loading}>
                    {loading ? 'Loading...' : 'Get Estimate'}
                </button>
            </form>

            {carbonFootprint && (
                <div>
                    <h3>Estimated Carbon Footprint</h3>
                    <pre>{JSON.stringify(carbonFootprint, null, 2)}</pre>
                </div>
            )}
        </div>
    );
};

export default CarbonFootPrint;
